        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search Users</button>

      <ul>
        {searchResult.map((pension) => (
          <li key={pension.id}>
            <p>Name: {pension.name}</p>
            <p>주소</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

