import React, { useEffect,useState } from 'react';
import Header from './Header';
import '../css/PensionMap.css';
import axios from 'axios';

const PensionMap = () => {

const [pensionData,setPensionData]=useState([]);

  useEffect(() => {

    axios.get('http://localhost:8081/penpick/pensionList')
    .then(response=>{
      setPensionData(response.data);
      loadKakaoMap(response.data);
    })
    .catch(error=>console.log('에러가 났어요ㅠㅠ => ',error));
  }, []); // 빈 배열을 넣어 한 번만 실행되도록 설정


  const loadKakaoMap=(data)=>{
    // 카카오맵 API 스크립트 동적으로 로드
    const script = document.createElement('script');
    script.async = true;
    script.src =
      'https://dapi.kakao.com/v2/maps/sdk.js?appkey=5f5613b170ddc98c39f71811791f5fc8&autoload=false';
    document.head.appendChild(script);

    script.onload = () => {
      window.kakao.maps.load(() => {
        const container = document.getElementById('map');
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.978), // 초기 지도 중심 좌표 (서울)
          level: 3, // 초기 지도 확대 레벨
        };

        // 지도 생성
        const map = new window.kakao.maps.Map(container, options);

        // 마커를 표시할 위치와 title 객체 배열입니다 
        for(var i = 0; i < data.length; i ++){
          const marker = new window.kakao.maps.Marker({
            map:map,
            position : new window.kakao.maps.LatLng(pension.latitude,pension.longitude),
            title:pension.name,
          });

          var iwContent = `<div style="padding:5px;height:120px">${pension.name}
           <br>${pension.address}<br><a href="http://localhost:3000/PensionList" style="color:blue" target="_blank">상세보기</a> 
          </div>`, // 인포윈도우에 표출될 내용으로 HTML 문자열이나 document element가 가능합니다
            iwPosition = new window.kakao.maps.LatLng(33.450701, 126.570667); //인포윈도우 표시 위치입니다

        // 인포윈도우를 생성합니다
        var infowindow = new  window.kakao.maps.InfoWindow({
            position : iwPosition, 
            content : iwContent 
        });
          
       // 마커에 클릭이벤트를 등록합니다
      window.kakao.maps.event.addListener(marker, 'click', function() {
        // 마커 위에 인포윈도우를 표시합니다
        infowindow.open(map, marker);  
      });
        }
      });
    };
  };

  return (
    <div>
      <Header />
      <p id='MapTitle'>지도로 검색하기</p>
      <div
        id='map'
        style={{ width: '80%', height: '700px', margin: 'auto' }}
      ></div>
    </div>
  );
};

export default PensionMap;
