import React, { useEffect } from 'react';
import Header from './Header';
import '../css/PensionMap.css';

const PensionMap = () => {
  useEffect(() => {
    // 카카오맵 API 스크립트 동적으로 로드
    const script = document.createElement('script');
    script.async = true;
    script.src =
      'https://dapi.kakao.com/v2/maps/sdk.js?appkey=5f5613b170ddc98c39f71811791f5fc8&autoload=false';
    document.head.appendChild(script);

    

    script.onload = () => {
      window.kakao.maps.load(() => {
        const container = document.getElementById('map');
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.978), // 초기 지도 중심 좌표 (서울)
          level: 3, // 초기 지도 확대 레벨
        };

        // 지도 생성
        const map = new window.kakao.maps.Map(container, options);

        // 마커 추가
        const markerPosition = new window.kakao.maps.LatLng(37.5665, 126.978); // 마커 좌표 (서울)
        const marker = new window.kakao.maps.Marker({
          position: markerPosition,

          
        });
        var content = '<div class="wrap">' + 
            '    <div class="info">' + 
            '        <div class="title">' + 
            '            카카오 스페이스닷원' + 
            '            <div class="close" onclick="closeOverlay()" title="닫기"></div>' + 
            '        </div>' + 
            '        <div class="body">' + 
            '            <div class="img">' +
            '                <img src="https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/thumnail.png" width="73" height="70">' +
            '           </div>' + 
            '            <div class="desc">' + 
            '                <div class="ellipsis">제주특별자치도 제주시 첨단로 242</div>' + 
            '                <div class="jibun ellipsis">(우) 63309 (지번) 영평동 2181</div>' + 
            '                <div><a href="https://www.kakaocorp.com/main" target="_blank" class="link">홈페이지</a></div>' + 
            '            </div>' + 
            '        </div>' + 
            '    </div>' +    
            '</div>';
        var overlay = new window.kakao.maps.CustomOverlay({
          content: content,
          map: map,
          position: marker.getPosition()       
      });
      // 마커를 클릭했을 때 커스텀 오버레이를 표시합니다
        new window.kakao.maps.event.addListener(marker, 'click', function() {
          overlay.setMap(map);
        });

        // 커스텀 오버레이를 닫기 위해 호출되는 함수입니다 
        function closeOverlay() {
          overlay.setMap(null);     
        }
              
        marker.setMap(map);
        marker.setDraggable(true); 
      });
    };
  }, []); // 빈 배열을 넣어 한 번만 실행되도록 설정

  return (
    <div>
      <Header />
      <p id='MapTitle'>지도로 검색하기</p>
      <div
        id='map'
        style={{ width: '80%', height: '700px', margin: 'auto' }}
      ></div>
    </div>
  );
};

export default PensionMap;
