import React, { useEffect } from 'react';
import Header from './Header';
import '../css/PensionMap.css';

const PensionMap = () => {
  useEffect(() => {
    const script = document.createElement('script');
    script.async = true;
    script.src = 'https://dapi.kakao.com/v2/maps/sdk.js?appkey=5f5613b170ddc98c39f71811791f5fc8&libraries=services';
    
    document.head.appendChild(script);

    script.onload = () => {
      window.kakao.maps.load(() => {
        const geocoder = new window.kakao.maps.services.Geocoder();
        const container = document.getElementById('map');
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.978),
          level: 3,
        };

        const map = new window.kakao.maps.Map(container, options);

        const marker = new window.kakao.maps.Marker();
        const infowindow = new window.kakao.maps.InfoWindow({ zindex: 1 });

        window.kakao.maps.event.addListener(map, 'click', function(mouseEvent) {
          searchDetailAddrFromCoords(mouseEvent.latLng, function(result, status) {
            if (status === window.kakao.maps.services.Status.OK) {
              var detailAddr = !!result[0].road_address ? '<div>도로명주소 : ' + result[0].road_address.address_name + '</div>' : '';
              detailAddr += '<div>지번 주소 : ' + result[0].address.address_name + '</div>';

              var content = '<div class="bAddr">' +
                              '<span class="title">법정동 주소정보</span>' + 
                              detailAddr + 
                            '</div>';

              marker.setPosition(mouseEvent.latLng);
              marker.setMap(map);

              infowindow.setContent(content);
              infowindow.open(map, marker);
            }   
          });
        });

        window.kakao.maps.event.addListener(map, 'idle', function() {
          searchAddrFromCoords(map.getCenter(), displayCenterInfo);
        });

        function searchAddrFromCoords(coords, callback) {
          geocoder.coord2RegionCode(coords.getLng(), coords.getLat(), callback);
        }

        function searchDetailAddrFromCoords(coords, callback) {
          geocoder.coord2Address(coords.getLng(), coords.getLat(), callback);
        }

        function displayCenterInfo(result, status) {
          if (status === window.kakao.maps.services.Status.OK) {
            var infoDiv = document.getElementById('centerAddr');
            for (var i = 0; i < result.length; i++) {
              if (result[i].region_type === 'H') {
                infoDiv.innerHTML = result[i].address_name;
                break;
              }
            }
          }    
        }

        // 주소 검색 예제
        geocoder.addressSearch('서울 강남구 역삼동 10', (result, status) => {
          if (status === window.kakao.maps.services.Status.OK) {
            console.log('변환된 좌표: ', result[0].x, result[0].y);
          } else {
            console.error('주소 검색 실패');
          }
        });
      });
    };
  }, []);

  return (
    <div>
      <Header />
      <p id='MapTitle'>지도로 검색하기</p>
      <div
        id='map'
        style={{ width: '80%', height: '700px', margin: 'auto' }}
      ></div>
    </div>
  );
};

export default PensionMap;
