                <button
                  id='filterButton'
                  onClick={() => handleFilter(1)}
                  type='button'
                >
                  수영장
                </button>
                <button
                  id='filterButton'
                  onClick={() => handleFilter(2)}
                  type='button'
                >
                  바베큐장
                </button>
                <button
